--
-- 表的结构 `meal_days`
--

CREATE TABLE `meal_days` (
  `id` int(11) NOT NULL,
  `day` varchar(255) DEFAULT NULL,
  `breakfast` varchar(255) DEFAULT NULL,
  `lunch` varchar(255) DEFAULT NULL,
  `dinner` varchar(255) DEFAULT NULL,
  `content` varchar(1000) NOT NULL,
  `b_img` varchar(128) NOT NULL,
  `l_img` varchar(128) NOT NULL,
  `d_img` varchar(128) NOT NULL,
  `img` varchar(128) NOT NULL,
  `dayId` int(11) DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

--
-- 转存表中的数据 `meal_days`
--

INSERT INTO `meal_days` (`id`, `day`, `breakfast`, `lunch`, `dinner`, `content`, `b_img`, `l_img`, `d_img`, `img`, `dayId`) VALUES
(1, 'Day1', '一片全麦吐司+两匙花生酱、半颗葡萄柚、一杯黑咖啡或茶（含咖啡因）', '一片全麦吐司、半罐水煮鲔鱼罐头、一杯黑咖啡或茶（含咖啡因）', '85g的肉（任何肉类都可以）、一杯（约110g）绿豆或是绿色蔬菜、一颗苹果、半根香蕉、一杯（约110g）香草冰淇淋', '说明：这个食谱是透过间歇性断食以及食材搭配产生化学作用，来让体重下降，所以食谱内容、甚至是进食顺序都很严格，不能更动。对于吃的东西很严格、3天之内都不能更换，第4天开始则可以正常进食、但必须是每餐不超过1500卡的低卡食物，只要进行一周体重就会明显下降！如果想要加速瘦身可以搭配一些轻度运动（因为吃这个食谱会没力、不要做高强度运动喔），另外也不建议长期为之，想健康的瘦下来还是要饮食管理+运动才是正解啦', 'b.jpg', 'l.jpg', 'd.jpg', 'large.jpg', 1),
(2, 'Day1', '全麦吐司1片，牛奶一杯，苹果一个', '芹菜粥：芹菜100克，大米100克，小米100克', '生菜沙拉一份+(盐、醋调味)', '', 'slarge.jpg', '', '', 'simg.jpg', 2),
(3, 'Day2', '全麦吐司2片，酸奶一杯，葡萄干10粒', '水煮鸡胸肉沙拉：鸡胸肉100克，水果蔬菜200克', '水蒸蛋1份', '', 'slarge.jpg', '', '', 'simg.jpg', 2),
(4, 'Day1', '酸奶一杯，水煮蛋2个', '红豆薏米粥(450克)', '苹果一个，坚果15克', '', 'slarge.jpg', '', '', 'simg.jpg', 2),
(5, 'Day4', '早餐：番薯粥，约450克', '手撕鸡胸肉，凉拌黄瓜', '橙子一个，葡萄干10粒', '', 'slarge.jpg', '', '', 'simg.jpg', 2),
(6, 'Day5', '燕麦泡牛奶约300克', '素菜沙拉一盘，番薯一个', '水蒸蛋一份', '', 'slarge.jpg', '', '', 'simg.jpg', 2),
(7, 'Day6', '全麦吐司2片，水煮蛋一个', '煎牛排一份，搭配生菜', '小番茄10个，坚果15克', '', 'slarge.jpg', '', '', 'simg.jpg', 2),
(8, 'Day7', '三明治一份，牛奶一杯', '土豆泥一份，水煮蛋2个，去蛋黄', '水果沙拉一份', '', 'slarge.jpg', '', '', 'simg.jpg', 2),
(9, 'Day1', '一杯黑咖啡', '两个水煮鸡蛋；水煮菠菜（不限量）；一个西红柿', '一块牛排（约200g）；新鲜生菜（不限量）', '睡足觉，控制好饮食，淀粉日，还是少淀粉，多吃菜，每餐量米饭不超过半碗，就行，不能吃蛋白质相关的食物肉，蛋，豆腐等。淀粉日不会瘦太多。肉蛋加蔬菜日，一定会瘦，但是全天不碰淀粉，除了不可避免的糖之外，不要单独吃糖，不能喝牛奶。这份菜单可以改善你的新陈代谢，并持续两年时间，这就意味着一旦你完成该食谱，恢复正常饮食也并不会是你复胖，这不是一般的食谱，他能帮助你燃烧更多热量，并在你结束食谱后仍然有效', 'b.jpg', 'c.jpg', '', 'a.jpg', 4),
(10, 'Day2', '一杯黑咖啡', '一盒酸奶（约200g）；低脂火腿（约200g）', '一块牛排（约200g）；新鲜生菜（不限量）', '', '', '', '', '', 4),
(11, 'Day3', '一杯黑咖啡；一片土司面包', '低脂火腿（约200g）；两个水煮鸡蛋；新鲜生菜（不限量）', '新鲜芹菜（不限量）；一个西红柿；一个苹果', '', '', '', '', '', 4),
(12, 'Day4', '一杯黑咖啡；一片土司面包', '一盒酸奶（约200g）；鲜榨橙汁（约200g）', '一个水煮鸡蛋；一根胡萝卜；白干酪（约200g）', '', '', '', '', '', 4),
(13, 'Day5', '一根胡萝卜', '熟鳕鱼（约200g）', '一块牛排（约200g）；新鲜生菜（不限量）；新鲜芹菜（不限量）', '', '', '', '', '', 4),
(14, 'Day6', '一杯黑咖啡；一片土司面包', '两个水煮鸡蛋；一根胡萝卜', '鸡肉（约250g）；新鲜生菜（不限量）', '', '', '', '', '', 4),
(15, 'Day7', '一杯茶（不加糖）', '不吃（大量喝水）', '一个苹果；一块羊肉（约200g）', '', '', '', '', '', 4),
(16, 'Day8', '一杯黑咖啡', '两个水煮鸡蛋；水煮菠菜（不限量）；一个西红柿', '一块牛排（约200g）；新鲜生菜（不限量）', '', '', '', '', '', 4),
(17, 'Day9', '一杯黑咖啡', '一盒酸奶（约200g）；低脂火腿（约200g）', '一块牛排（约200g）；新鲜生菜（不限量）', '', '', '', '', '', 4),
(18, 'Day10', '一杯黑咖啡；一片土司面包', '低脂火腿（约200g）；两个水煮鸡蛋；新鲜生菜（不限量）', '新鲜芹菜（不限量）；一个苹果；一个西红柿', '', '', '', '', '', 4),
(19, 'Day11', '一杯黑咖啡；一片土司面包', '一盒酸奶（约200g）；鲜榨橙汁（约200g）', '一个水煮鸡蛋；一根胡萝卜；白干酪（约200g）', '', '', '', '', '', 4),
(20, 'Day12', '一根胡萝卜', '熟鳕鱼（约200g）', '一块牛排（约200g）；新鲜芹菜（不限量）；新鲜生菜（不限量）', '', '', '', '', '', 4),
(21, 'Day13', '一杯黑咖啡；一片土司面包', '两个水煮鸡蛋；一根胡萝卜', '鸡肉（约250g）；新鲜生菜（不限量）', '', '', '', '', '', 4),
(22, 'Day1', '在三餐时间可以无限吃水果（香蕉除外，推荐瓜类）。早上喝一杯茶或咖啡。\r\n饿了或者疲惫了可以随时喝蔬菜汤。', NULL, NULL, '法国很流行的一款快速减肥保健品，需要按食谱吃——804，即8天0负担4公斤。\r\n15年第一次尝试的确掉了7斤，没有反弹。后来又巩固了一次，保持在了100斤。去年毕业压力+上班过劳又丰满回来了，只好再次试试804“魔法”。\r\n不过与其定义它为减肥，我更习惯称它轻断食。既非绝食又很合理的帮助排毒，还是很适合现代人营养过剩的身体状态的。而且每日的食谱都有不限制量可以吃到饱的食物，对于不想挨饿的人来说简直是福音。\r\n这里只写804推荐的食谱！！！\r\n蔬菜汤可以自己熬，里面含有洋葱番茄甘蓝和红辣椒。我买的是现成的，有一点点方便面味，刚开始接受不了，后来也就习惯了～', 'gb.jpg', 'gd.jpg', 'gl.jpg', '', 3),
(23, 'Day2', '在三餐时间可以无限吃蔬菜（新鲜冷冻的都可以，做菜时不要放油脂类的辅料，尽量避免豌豆、扁豆、玉米，建议绿色蔬菜）。早上喝一杯茶或咖啡。\r\n饿了或者疲惫了可以随时喝蔬菜汤。', NULL, NULL, '', '', '', '', '', 3),
(24, 'Day3', '在三餐时间可以无限吃前两日准许吃的水果和蔬菜（备注同上）。早上喝一杯茶或咖啡。\r\n饿了或者疲惫了可以随时喝蔬菜汤。', NULL, NULL, '', '', '', '', '', 3),
(25, 'Day4', '在三餐时间可以无限吃脱脂的乳制品和3-8根香蕉，早上喝一杯茶或咖啡。\r\n（乳制品可以是脱脂奶、脱脂酸奶、脱脂原味优酪乳，它们可以为身体提供蛋白质和钙；如果不爱吃香蕉可以换成全麦面条，用水煮，调味料里不要含脂肪）\r\n饿了或者疲惫了可以随时喝蔬菜汤，并且可以在里面加入一些蔬菜。', NULL, NULL, '', '', '', '', '', 3),
(26, 'Day5', '在三餐时间可以吃总共300g的肉类（或3-4个鸡蛋）和6个新鲜的番茄。早上喝一杯茶或咖啡。\r\n至少喝一大碗蔬菜汤。', NULL, NULL, '', '', '', '', '', 3),
(27, 'Day6', '在三餐时间可以吃最多300g的肉类以及蔬菜。肉类要与蔬菜同食，调料里不要含脂肪。早上喝一杯茶或咖啡。\r\n至少喝一大碗蔬菜汤。', NULL, NULL, '', '', '', '', '', 3),
(28, 'Day7', '在三餐时间可以无限吃糙米和蔬菜（调料里不要含脂肪）。早上喝一杯茶或咖啡。\r\n饿了或者疲惫了可以随时喝蔬菜汤。', NULL, NULL, '', '', '', '', '', 3),
(29, 'Day8', '可以吃和第七天一样的食物。此外可以喝无糖的鲜榨果汁。\r\n饿了或者疲惫了可以随时喝蔬菜汤。', NULL, NULL, '', '', '', '', '', 3);

