--
-- 表的结构 `menu`
--

CREATE TABLE `menu` (
  `mid` int(11) NOT NULL,
  `mname` varchar(36) DEFAULT NULL,
  `img` varchar(36) DEFAULT NULL,
  `video` varchar(255) DEFAULT NULL,
  `subtitle` varchar(20000) DEFAULT NULL,
  `degree` varchar(36) DEFAULT NULL,
  `taste` varchar(36) DEFAULT NULL,
  `time` varchar(36) DEFAULT NULL,
  `tid` int(255) NOT NULL,
  `grade` decimal(2,1) NOT NULL,
  `watch` int(128) NOT NULL,
  `store` int(128) NOT NULL,
  `tips` varchar(255) NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

--
-- 转存表中的数据 `menu`
--

INSERT INTO `menu` (`mid`, `mname`, `img`, `video`, `subtitle`, `degree`, `taste`, `time`, `tid`, `grade`, `watch`, `store`, `tips`) VALUES
(1, '小白兔花样馒头', 'menu1.jpg', NULL, '好多人让我做些简单造型的，花样馒头，那么今天就分享一款，超级简单，又呆萌可爱的小白兔花样馒头吧，孩子看到后，能多吃一口饭，长高一厘米。', '容易', '香甜', '20', 1, '5.0', 0, 4, '馒头蒸好后，先别急着揭开锅盖，闷上七八分钟再揭，否则会容易热胀冷缩使馒头毁掉。酵母的用量夏天少，冬天多。'),
(2, '减肥鸡胸肉意面', 'menu5.jpg', NULL, '其实，减肥不一定是水煮菜～ 也可以是很美味的～', '容易', '清淡', '40', 2, '6.0', 0, 0, ''),
(3, '猫咪亲子便当', 'menu9.jpg', 'x.mp4', '可爱的猫咪亲子便当搭配水果和豆浆，既营养还可以让便当餐看起来萌哒哒，这样有颜值的便当快来动手试试吧~', '容易', '清淡', '40', 3, '6.0', 0, 0, ''),
(4, '瘦身早餐沙拉', 'menu2.jpg', 'x.mp4', '早餐这种东西，既要吃的营养丰盛，又要不长肉，那我想一份蔬菜沙拉加水煮蛋的搭配，一定能满足你的要求。', '容易', '清淡', '5', 1, '6.0', 0, 0, ''),
(5, '减肥吃草午餐', 'menu6.jpg', NULL, '懒人快速版', '容易', '香甜', '30', 2, '6.0', 0, 0, ''),
(6, '减肥期早餐烤燕麦', 'menu3.jpg', 'x.mp4', '早餐烤燕麦，味道好吃，酥酥的感觉，让你绝对喜欢这种美味，让你爱上燕麦的感觉，绝对是不一样的。', '容易', '清淡', '5', 1, '6.0', 0, 0, ''),
(7, '紫薯卷热可可', 'menu4.jpg', NULL, '用一杯热可可开启的早晨，一定是温暖而又幸福的，再加上一个紫薯卷，美味又饱腹。减脂塑形必备款', '中等', '微甜', '30', 1, '6.0', 0, 0, ''),
(8, '韩式拌饭(糙米饭)', 'menu7.jpg', NULL, '减脂期间吃大米饭有罪恶感，所以选择了用糙米饭来做，降低了罪恶感，而且作为减肥餐又营养又耐饿~', '中等', '清淡', '30', 2, '5.0', 0, 0, ''),
(9, '低卡营养午餐', 'menu8.jpg', 'x.mp4', '只要做到营养均衡＋运动！体重才会有效的下降！', '容易', '香甜', '30', 2, '5.0', 0, 0, ''),
(10, '香煎马鲛鱼', 'menu10.jpg', NULL, '每年的清明节前后，是马鲛鱼上市的时节，这种鱼肉质细嫩洁白，味道糯软鲜爽，营养价值高但胆固醇含量极低，是味美价廉的美食！一般马鲛鱼都是与咸菜蘼同煮，今天我换个方式，试着用煎三文鱼的法子做了道香煎马鲛鱼，味道出奇的好！', '容易', '清淡', '20', 3, '5.0', 0, 0, ''),
(11, '蒜香孜然烤虾', 'menu11.jpg', NULL, '虾营养价值高，含有比较丰富的蛋白质。蛋白质是生命的基本物质，且其肉质松软，易消化，多吃营养高，易消化吸收的食物，孕妇子宫和乳腺增生等都需要大量蛋白质，所以孕妇要多吃含蛋白质的食物。', '容易', '咸香', '30', 3, '4.0', 0, 0, ''),
(12, '蒜茸丝瓜柱', 'menu12.jpg', NULL, '丝瓜的热量很低，100克中只有20大卡，脂肪含量也不高，吃了之后不会出现长胖的现象，非常有利于减肥。丝瓜能加速体能快速的排毒过程，帮助提升新陈代谢，且含热量也很低。另外丝瓜含有多种微量元素，热量低，水分多，非常适合在减肥期间食用的', '容易', '清淡', '20', 3, '6.0', 0, 0, ''),
(13, '香煎鸡胸+蒜蓉西兰花', 'list21.jpg', NULL, '西兰花中的营养成分,不仅含量高,而且十分全面,主要包括蛋白质、碳水化合物、脂肪、矿物质、维生素C和胡萝卜素等。据分析,每100克新鲜西兰花的花球中,含蛋白质3.5克—4.5克,是菜花的3倍、番茄的4倍。此外,西兰花中矿物质成分比其他蔬菜更全面,钙、磷、铁、钾、锌、锰等含量都很丰富,比同属于十字花科的白菜花高出很多。', '容易', '清淡', '20', 1, '8.0', 1, 1, ''),
(14, '瘦身沙拉', 'meal2.jpg', 'x.mp4', '营养丰富，减肥效果棒棒嗒，让你减肥路上不挨饿也掉秤！', '容易', '甜香', '10', 2, '7.0', 2, 2, ''),
(15, '瘦身凉面', 'meal5.jpg', NULL, '全燕麦面，低卡高纤，爽滑劲道，最关键的是吃了不发胖', '容易', '咸香', '25', 3, '7.0', 3, 3, ''),
(16, '凉拌麻辣鸡丝', 'meal8.jpg', NULL, '麻辣鸡丝做起来很方便快捷，麻辣鲜香很开胃，既是减肥菜也是懒人菜啦，很适合上班族。', '较难', '麻辣', '35', 1, '7.0', 4, 2, ''),
(17, '无油煎牛肉片', 'meal9.jpg', NULL, '无油无糖无酱油', '容易', '咸香', '30', 2, '7.0', 5, 1, ''),
(18, '彩椒鸡肉卷', 'meal10.jpg', NULL, '三分练七分吃。自从我开始减肥后，也开始琢磨怎样吃的健康又美味，当然还要简单。鸡胸肉是大家都推荐的高蛋白低脂肉类，今天加彩椒炒了用饼一卷，快手又好吃，非常适合一个人的简餐，推荐给大家。', '容易', '咸香', '35', 3, '7.0', 6, 5, ''),
(19, '健康玉米粒', 'meal11.jpg', NULL, '简单到爆炸！猴健康', '简单', '甜香', '10', 1, '7.1', 7, 3, ''),
(20, '虾滑丝瓜蒸蛋羹', 'meal12.jpg', NULL, '加少许油在鸡蛋羹里一起打匀,加上采用新鲜虾 去壳去虾线 加盐巴料酒剁泥', '容易', '咸香', '35', 2, '6.1', 1, 2, ''),
(21, '青菜虾', 'meal13.jpg', NULL, '高蛋白营养丰富', '简单', '家常', '15', 3, '6.2', 2, 3, ''),
(22, '魔芋凉皮', 'meal14.jpg', NULL, '0热量0脂肪的魔芋绝对是代餐的最佳选择', '容易', '微辣', '25', 1, '6.3', 3, 4, ''),
(23, '青笋杏鲍菇', 'meal15.jpg', NULL, '口味清淡，营养丰富', '容易', '清淡', '25', 2, '6.4', 4, 2, ''),
(24, '豆渣麦麸饼', 'meal16.jpg', 'x.mo4', '做法简单，很好的饱腹感，适合减肥的时候吃，可以控制每天的饮食', '容易', '微甜', '25', 3, '6.5', 4, 2, ''),
(25, '如意巴沙鱼卷', 'meal1.jpg', NULL, '传统如意卷是用草鱼做的，要剔骨什么的，比较麻烦，于是改用巴沙鱼，简单，美味又营养。', '简单', '家常', '15', 1, '6.6', 3, 2, ''),
(26, '红豆薏米粥', 'meal4.jpg', NULL, '早晨起来给自己一碗暖暖的红豆薏米粥，营养丰富又舒服?～', '容易', '微甜', '35', 0, '6.7', 2, 3, ''),
(27, '番茄鸡肉丸', 'meal17.jpg', NULL, '鸡胸肉蛋白质含量较高，且易被人体吸收入利用，有增强体力，强壮身体的作用，所含对人体生长发育有重要作用的磷脂类，是中国人膳食结构中脂肪和磷脂的重要来源之一。同时鸡肉有益五脏，补虚损，补虚健胃、强筋壮骨、活血通络、调月经、止白带等作用。', '容易', '清淡', '25', 0, '6.1', 4, 3, ''),
(28, '糙米菠萝炒饭', 'meal18.jpg', NULL, '菠萝的确清甜加上虾仁的Q弹', '容易', '微甜', '25', 0, '6.2', 1, 1, ''),
(29, '西红柿鸡蛋炖土豆', 'meal19.jpg', NULL, '土豆就代替了主食～碳水化合物～鸡蛋就是蛋白质～西红柿里面含有很多维生素～这样低热量又可以饱腹的事物～酸酸甜甜～最适合夏天减肥', '容易', '清淡', '25', 0, '6.3', 2, 3, ''),
(30, '番茄烤鸡蛋', 'meal21.jpg', NULL, '这款可以做早中晚三餐～～都很合适！讲道理其实是要去掉一个蛋黄的，但是..太懒啦就两个全蛋了。减肥餐一般推荐两白一黄。', '较难', '咸香', '15', 0, '6.5', 3, 4, ''),
(31, '椒麻手撕鸡', 'meal22.jpg', NULL, '牛肉鸡肉成了我每日的主菜。对不爱吃鸡的我来说，这道手撕鸡能让我开胃饱腹，在减肥的同时也保证了营养。同时，一份鸡肉两种酱料，让不吃辣椒的人也能享受美食。', '较难', '麻辣', '25', 0, '6.6', 4, 1, ''),
(33, '香菇肉馅饺子', 'list1.jpg', NULL, '中国的面食博远深奥！可以塑造任何食物，饺子就是中国最具代表的食物！饺子代表团圆，热闹！幸福！今天分享的是香菇肉馅味的！', '普通', '家常鲜嫩', '45分钟', 0, '7.2', 2, 2, ''),
(34, '酱烧豆腐', 'list3.jpg', NULL, '豆腐，我国自古就有的美食，一直到今天，豆腐都是家常饮食中不可或缺的一分子。常食豆腐，有益于人体神经、血管、大脑的生长发育。豆腐不含胆固醇，为高血压、高血脂、高胆固醇症及动脉硬化、冠心病患者的药膳佳肴。豆腐含有丰富的植物雌激素，对防治骨质疏松症有良好的作用。但很多人不喜欢食用豆腐。虽然知道它营养丰富，但就是好多人不爱吃。说起豆腐，就会想起麻婆豆腐，豆腐最经典的做法之一。但不能总是吃麻婆豆腐吧？总得换换口味吧？关于豆腐，我有几种私房做法，绝对的素菜，但却有着比荤菜还棒的味道及口感，今天暂且分享其中一种。', '容易', '咸香', '15分钟', 0, '7.0', 3, 2, ''),
(35, '减脂鲜蔬汤', 'list2.jpg', NULL, '最减脂的汤水，少油少盐，一碗管饱。番茄含有丰富的胡萝卜素、维生素C和B族维生素。番茄含的“番茄素”，有抑制细菌的作用；含的苹果酸、柠檬酸和糖类，有助消化的功能。番茄含有丰富的营养，又有多种功用被称为神奇的菜中之果。鸡毛菜所含的矿物质能够促进骨骼的发育，加速人体的新陈代谢和增强机体的造血功能，胡萝卜素、烟酸等营养成分，也是维持生命活动的重要物质。姬菇含有抗肿瘤细胞的硒、多糖体等物质，对肿瘤细胞有很强的抑制作用，且具有免疫特性。这碗汤水高颜值高营养，大家一起动手做起来。', '容易', '咸鲜', '15分钟', 0, '8.0', 1, 3, ''),
(36, '橄榄油西兰花饭团', 'list9.jpg', NULL, '很多人都喜欢吃寿司和包菜，那相信大家对于我今天即将要分享的这款饭团也不会讨厌。我家常做的这款饭团，做法更为简单，而营养和口感却非常之好。在这款饭团里，我用到了营养丰富的蔬菜：西兰花。西兰花是所有蔬菜中最闪亮的抗癌明星，我们平时一定要多吃。', '容易', '清淡', '10分钟', 0, '7.6', 4, 5, ''),
(37, '清蒸鲈鱼', 'list15.jpg', NULL, '“鲜”这个字中，鱼占了一半的江山，可见鱼何其鲜美。为了最大限度地体现鱼的鲜美，清蒸无疑是上策，俗话说清蒸是对鱼最高的赞礼，也是对食客的尊敬。下班后，不想拖着疲惫的身体在厨房捣鼓半天，就来做一道清蒸鲈鱼吧，清蒸鲈鱼做法简单，味道鲜美，而且营养丰富，不容错过哦。', '容易', '清淡', '15分钟', 0, '7.4', 3, 6, ''),
(38, '菠菜网卷饼', 'list7.jpg', NULL, '菠菜含有丰富的胡萝卜素及维生素并且矿物元素含量极好其中的铁元素尤其突出。经常食用菠菜具有补血补铁的作用还能促进新陈代谢防止便秘等，孩子经常食用不仅能促进发育成长还能保护眼睛维护视力。所以把菠菜列入辅食菜单是很不错的！把菠菜直接炒成菜，孩子不一定喜欢。所以花点心思换个花样试试.....', '普通', '甜鲜', '60分钟', 0, '7.8', 5, 8, ''),
(39, '香菇鸡肉滑粥', 'list12.jpg', NULL, '鸡肉粥含有丰富的蛋白质和人体所需要的脂肪、维生素等，营养丰富、益气养血、补肾养阴等功效。同时能为儿童期的生长发育提供所需的多种营养素。今天分享的这个也算是一道备受青睐的家常食谱，早上吃很不错哦…', '简单', '咸香', '30分钟', 0, '7.5', 7, 3, ''),
(40, '蒜香秋葵', 'list19.jpg', NULL, '今天的家常美食要介绍的是一个神奇的蔬菜，在最初引进的时候可以说是风靡一片。人人被它的味道和口感折服，但是最主要的全是看中它的营养价值。这个神奇的小蔬菜就是“秋葵”秋葵含有丰富的营养价值。高蛋白质、高矿物元素、低脂肪、低热量、无胆固醇这是追求健康者与减肥运动者首选蔬菜之一，经常食用秋葵除了能补充必要的营养价值。还有很好的食疗做用，比如补充体能消除疲劳、降低血糖，还能养颜美容强身补肾。', '容易', '咸香', '30分钟', 0, '7.7', 32, 23, ''),
(41, '番茄鸡蛋卷', 'list18.JPG', NULL, '番茄（西红柿）与鸡蛋的组合可谓是天生一对，两样食材的营养价值都特别高，番茄富含胡萝卜素和维生素，能够保护心血管，而鸡蛋也是我们日常生活中不可或缺的高营养食物。最经典的做法莫过于番茄炒蛋了，但再好吃的菜，多吃也腻。不如换个做法来当早餐吃。鸡蛋卷起来，口感丰富细腻，有葱香、鸡蛋香和番茄的酸味，味道棒棒哒，好吃又有营养。简单的快手做法，小朋友也喜欢。', '容易', '清香', '20分钟', 0, '7.4', 52, 53, ''),
(42, '瓦伊森', 'list17.jpg', NULL, '瓦伊森是德国面包的基础款，Weizen在德语里是小麦的意思，这款面包含油量非常低，很符合德国人对面包的理解：富含营养又最健康的天然食品。瓦伊森面包经过烘烤后，麦香诱人，咬一口外皮酥脆，内里口感扎实，嘟嘟的唇形外观让人陷入小麦的香气魅力中不能自拔。', '容易', '甜香', '3小时', 0, '7.6', 45, 44, ''),
(43, '三黑粥', 'list16.jpg', NULL, '冬天是补肾的季节，而黑色入肾，此时多吃黑色食物不仅御寒保暖，还能增强我们肾脏的机能。而三黑粥无疑是很好的食疗粥，俗话说“肾气足，百病除”。常喝三黑粥，肾气十足！肾好了，才有好的身体。三黑粥用到三种基本黑色食材：黑豆、黑米、黑芝麻，另外再放点核桃仁，这粥口感浓稠美味，营养丰富，常食能滋补肝肾，补脑益智，白头发也渐渐少了，还对延缓衰老也有一定功效。', '容易', '甜', '90', 0, '7.2', 23, 31, ''),
(44, '藜麦主食沙拉', 'list4.jpg', NULL, '丰富的物料和颜色充满了春天的感觉，虽然只是一道沙拉，但却绝对含有一顿餐的完整营养，其中营养平衡的物料和充盈的口感，即使多吃一些也不会给身体带来负担。', '容易', '清淡', '20', 0, '7.8', 14, 25, ''),
(45, '蒸藕盒', 'list3.jpg', NULL, '今天推荐大家一道蒸藕盒子。一听到藕盒，许多人会想到炸藕盒这种做法，炸藕盒是吃起来是不错，可是炸的东西吃多了太容易上火了，所以今儿就改蒸着吃，香香的肉馅中夹着脆脆的藕，简单，好吃，不仅可以最大限度的保留了营养，蒸的不上火，适合一家老小，招待客人都有面子，做法很简单！', '容易', '60', '家常', 0, '7.4', 23, 25, ''),
(46, '拍黄瓜', 'list5.jpg', NULL, '拍黄瓜必备芝麻油，因为芝麻油的香气是其他油都不能比拟的，拌凉菜时，少了它，总觉得少了点味道。', '容易', '15', '家常', 0, '7.3', 36, 42, ''),
(47, '健康蔬果沙拉', 'list13.jpg', NULL, '沙拉人人都爱，加了牛油果的蔬果沙拉，味道会不一样哦。', '容易', '微咸', '15', 0, '7.7', 45, 33, ''),
(48, '柠香鸡肉串', 'list11.jpg', NULL, '虽然是减肥餐，只要花点心思也能把食物做的极具诱惑力。色彩丰富的高纤蔬菜与鸡肉串成串，用烤箱烤制，刷上自制的低卡烧烤酱，瘦身期也能吃上几串美味的烤串，真是太幸福了！', '容易', '清淡', '20', 0, '7.9', 45, 32, ''),
(49, '南瓜沙拉', 'list25.jpg', NULL, '南瓜含有丰富的膳食纤维，吃了南瓜就会觉得很饱，不想再多吃，即使吃了很多也不会长胖，因为南瓜的热量很低，是很适合用于减脂的蔬果。南瓜营养丰富全面，对肠胃和血管都有很好的保护作用，用南瓜搭配其他的新鲜蔬菜做成的这道沙拉，很适合女生当作日常减脂餐，既饱腹也可口哦。', '容易', '清淡', '20', 0, '7.5', 12, 42, ''),
(50, '乡村素炖锅', 'list22.jpg', NULL, '暖暖的蔬菜汤，鲜的幂次方！', '容易', '咸香', '25', 0, '7.7', 343, 43, ''),
(51, '鳕鱼洋芋擦擦', 'list23.jpg', NULL, '土豆作为餐桌上的“变形金刚”入选第四大主粮，在陕北和甘肃都经常将土豆沾上面粉蒸熟后做成洋芋擦擦食用，而在陕西关中平原这样蒸制的洋芋擦擦也叫作“麦饭”。我生完宝宝后开始恢复健身，既要吃的健康又要美味饱腹还要满足身体哺乳需求。普通的陕西版本的油泼洋芋擦擦营养不够均衡并且食物较为单一，今天这个改良版的低卡饱腹的鳕鱼红玉洋芋擦擦不仅颜值高，还制作简便，适合健身人士或者减脂期食用。', '容易', '清淡', '30', 0, '7.6', 43, 32, ''),
(52, '奇亚籽雪慕斯早餐杯', 'list24.jpg', NULL, '奇亚籽因其零脂肪、高纤维、高蛋白质而广受大家喜欢。这道奇亚籽雪慕斯不仅适合早上食用，营养丰富，可以满足人体一天的营养需求。也可以作为下午茶的能量补给，营养又美味，非常适合减脂人群。', '容易', '甜香', '15', 0, '7.5', 45, 36, '');

