INSERT INTO `steps` (`sid`, `details`, `img`, `stepsId`) VALUES
(1, '红薯洗净备用', '5819a133N03021b26.jpg', 5),
(2, '电饭煲加水刚好没过红薯，用煮饭模式将红薯煮熟', '5819a133N03021b26.jpg', 5),
(3, '取煮熟的红薯两个，剥皮，再用叉子将红薯碾压成泥', '5819a133N03021b26.jpg', 5),
(4, '待红薯泥稍冷却后，用手将红薯泥整形成扁扁的长条状，放置在烤盘上', '5819a133N03021b26.jpg', 5),
(5, '红薯上均匀撒上白芝麻', '5819a133N03021b26.jpg', 5),
(6, '烤箱预热，180度放烤箱中层烤20分钟，宝宝喜欢口感硬一点的可以再烤久一点', '5819a133N03021b26.jpg', 5),
(7, '鸡胸肉洗净用30ml酱油腌制30分钟，腌制的同时洗净西兰花，掰成小瓣，用开水焯一分钟，捞出用凉水冲洗2次，能保持颜色翠绿，蒜切丁备用，锅里放入橄榄油烧制七成热加入蒜末炒出香味加入西兰花翻炒半分钟加入盐再炒半分钟就可以关火了，换一个不粘锅或者平底锅烧热，稍微放点点油，放入鸡胸肉小火两面煎至变色，可以放少量开水防止煎糊，加水后可以盖盖焖5分钟，注意翻面，然后可以都盛出摆盘开吃啦。\r\n', NULL, 13),
(8, '所有喜爱食物放入一大碗加入橄榄油或沙拉酱拌匀，最后洒上胡椒粉即可！', NULL, 14),
(9, '红薯洗净备用', '5819a133N03021b26.jpg', 5),
(10, '酵母用温水化开，然后分次加入到面粉里，搅拌成絮状，揉成光滑面团，盖上保鲜膜，进行发酵，冬天需要放到温暖的地方发酵。', 'xiaobaitu1.jpg', 1),
(11, '面团发酵好要看状态，体积明显两倍大，撕开有丰富的蜂窝组织。', 'xiaobaitu2.jpg', 1),
(12, '取出面团，进行揉面排气，一定要揉到面团，切开无任何气孔，即可，这样蒸出来的馒头表皮才光滑。', 'xiaobaitu3.jpg', 1),
(13, '取出发好的一点白面团，加适量的红曲米粉揉匀备用。', 'xiaobaitu4.jpg', 1),
(14, '红面团擀成面皮，用花形模具，压出花花，没有模具的，也可以自己制作蝴蝶结哦。', 'xiaobaitu5.jpg', 1),
(15, '白面团分成大小差不多的小剂子。\r\n', 'xiaobaitu6.jpg', 1),
(16, '取出一个小剂子，搓成一头细，一头圆的面团，然后，用剪刀剪开细的那头，成为小白兔的耳朵，再用牙签压下耳朵。', 'xiaobaitu7.jpg', 1),
(17, '粘上红色小花，用红豆做眼睛，小白兔就做好了，二次发酵会，冷水上锅，开中小火，一般蒸25分钟左右就行了。', 'xiaobaitu8.jpg', 1);

